import React, { useEffect, useState } from 'react';

interface ConfettiCelebrationProps {
  isVisible: boolean;
  message: string;
  onComplete: () => void;
  intensity?: 'low' | 'medium' | 'high';
}

// Confetti particle colors
const CONFETTI_COLORS = [
  '#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', 
  '#FECA57', '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3'
];

const ConfettiCelebration: React.FC<ConfettiCelebrationProps> = ({ 
  isVisible, 
  message, 
  onComplete, 
  intensity = 'medium' 
}) => {
  const [particles, setParticles] = useState<Array<{
    id: number;
    x: number;
    y: number;
    color: string;
    size: number;
    rotation: number;
    delay: number;
  }>>([]);

  // Generate confetti particles based on intensity
  useEffect(() => {
    if (!isVisible) {
      setParticles([]);
      return;
    }

    const particleCount = intensity === 'low' ? 15 : intensity === 'high' ? 40 : 25;
    const newParticles = Array.from({ length: particleCount }, (_, i) => ({
      id: i,
      x: Math.random() * 100, // Percentage across screen
      y: Math.random() * 20 + 10, // Start from top area
      color: CONFETTI_COLORS[Math.floor(Math.random() * CONFETTI_COLORS.length)],
      size: Math.random() * 8 + 4, // 4-12px
      rotation: Math.random() * 360,
      delay: Math.random() * 500, // Stagger animations
    }));

    setParticles(newParticles);

    // Auto-hide after animation completes
    const timer = setTimeout(() => {
      onComplete();
    }, 3000);

    return () => clearTimeout(timer);
  }, [isVisible, intensity, onComplete]);

  if (!isVisible) return null;

  return (
    <div className="fixed inset-0 pointer-events-none z-50 overflow-hidden">
      {/* Confetti Particles */}
      {particles.map((particle) => (
        <div
          key={particle.id}
          className="absolute animate-confetti"
          style={{
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            backgroundColor: particle.color,
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            borderRadius: '2px',
            transform: `rotate(${particle.rotation}deg)`,
            animationDelay: `${particle.delay}ms`,
            animationDuration: '2.5s',
          }}
        />
      ))}

      {/* Celebration Message */}
      <div className="fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 pointer-events-none">
        <div className="bg-white/95 backdrop-blur-sm border-2 border-green-400 shadow-xl rounded-2xl px-8 py-6 text-center animate-celebration-message">
          <div className="text-4xl mb-2">ðŸŽ‰</div>
          <div className="text-lg font-bold text-green-700 mb-1">Awesome!</div>
          <div className="text-sm text-gray-600 max-w-xs">{message}</div>
        </div>
      </div>

      {/* CSS Animations - Inline for simplicity */}
      <style>{`
        @keyframes confetti {
          0% {
            transform: translateY(0) rotate(0deg) scale(1);
            opacity: 1;
          }
          100% {
            transform: translateY(100vh) rotate(720deg) scale(0.5);
            opacity: 0;
          }
        }

        @keyframes celebration-message {
          0% {
            transform: translate(-50%, -50%) scale(0.5);
            opacity: 0;
          }
          20% {
            transform: translate(-50%, -50%) scale(1.1);
            opacity: 1;
          }
          80% {
            transform: translate(-50%, -50%) scale(1);
            opacity: 1;
          }
          100% {
            transform: translate(-50%, -50%) scale(0.8);
            opacity: 0;
          }
        }

        .animate-confetti {
          animation: confetti 2.5s ease-out forwards;
        }

        .animate-celebration-message {
          animation: celebration-message 3s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default ConfettiCelebration;
